function fixingSpaces(arr)  {
    var finalArr = arr;
    for(var i = 0; i < arr.length; i++) {
        var newArr = arr[i][1].split(" ");
        for(var j = 0; j < newArr.length; j++) {
            if(newArr[j] == "\n") {
                newArr[j] = "<br> <br>"
            } else if (newArr[j] == "\t") {
                newArr[j] = "&nbsp &nbsp";
            }
        }
        finalArr[i][1] = newArr.join(" ");
    }
    return finalArr;
}